//src/IndividualContacts.js
import React, { useState, useEffect } from 'react';
import { useLocation, Link } from 'react-router-dom';
import axios from 'axios';
import './styles.css';
import { FaCheckSquare, FaBell, FaMoneyBillWave, FaPlusCircle } from 'react-icons/fa';
import TodoTab from './TodoTab'; // Assuming TodoTab is a separate component

const IndividualContact = () => {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const name = params.get('name'); // Get the contact name from URL
  const avatar = params.get('avatar'); // Get the contact avatar URL or ID

  const [activeTab, setActiveTab] = useState('todo'); // State to track active tab
  const [todos, setTodos] = useState([]); // State for todos (fetched from the backend)

  // Fetch todos for the contact when component mounts
  useEffect(() => {
    const fetchTodos = async () => {
      try {
        // Replace this URL with your API to fetch todos for a specific contact
        const response = await axios.get(`http://localhost:5000/api/contacts/${params.get('contactId')}/todos`);
        setTodos(response.data);
      } catch (error) {
        console.error('Error fetching todos:', error);
      }
    };

    fetchTodos();
  }, [params.get('id')]); // Trigger fetch whenever the contact ID changes

  // Handle adding a new todo
  const addTodo = async (newTodoText) => {
    try {
      const newTodo = { text: newTodoText, completed: false, contactId: params.get('id') };
      const response = await axios.post('http://localhost:5000/api/todos', newTodo);
      setTodos([...todos, response.data]); // Add the new todo to the list
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  // Handle toggling todo completion
  const toggleTodo = async (todoId) => {
    try {
      const updatedTodo = todos.find(todo => todo._id === todoId);
      updatedTodo.completed = !updatedTodo.completed;
      const response = await axios.put(`http://localhost:5000/api/todos/${todoId}`, updatedTodo);
      setTodos(todos.map(todo => (todo._id === todoId ? response.data : todo)));
    } catch (error) {
      console.error('Error toggling todo completion:', error);
    }
  };

  return (
    <div className="individual-contact">
      <header className="header">
        <button className="back-button" onClick={() => window.history.back()}>← Contacts</button>
      </header>

      <div className="contact-summary">
        <div className="contact-photo">
          <img src={avatar || '/default-avatar.jpg'} alt={`${name}'s avatar`} />
        </div>
        <h2>{name || "Contact"}</h2>
      </div>

      <div className="tab-bar">
        <button
          className={`tab ${activeTab === 'todo' ? 'active' : ''}`}
          onClick={() => setActiveTab('todo')}
        >
          <FaCheckSquare className="icon" /> Todo
        </button>
        <button
          className={`tab ${activeTab === 'reminder' ? 'active' : ''}`}
          onClick={() => setActiveTab('reminder')}
        >
          <FaBell className="icon" /> Reminder
        </button>
        <button
          className={`tab ${activeTab === 'finance' ? 'active' : ''}`}
          onClick={() => setActiveTab('finance')}
        >
          <FaMoneyBillWave className="icon" /> Finance
        </button>
      </div>

      <div className="tab-content">
        {activeTab === 'todo' && (
          <div className="todo-content">
            <div className="todo-header">
              <button
                className="add-button"
                onClick={() => {
                  const newTodoText = prompt('Enter new todo:');
                  if (newTodoText) addTodo(newTodoText);
                }}
              >
                <FaPlusCircle className="icon" /> Add New Todo
              </button>
            </div>
            <div className="todo-list">
              {todos.length === 0 ? (
                <p>No todos found</p>
              ) : (
                todos.map(todo => (
                  <div key={todo._id} className={`list-item ${todo.completed ? 'completed' : ''}`}>
                    <input
                      type="checkbox"
                      checked={todo.completed}
                      onChange={() => toggleTodo(todo._id)}
                    />
                    <span>{todo.text}</span>
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {activeTab === 'reminder' && (
          <div className="reminder-content">
            <button className="add-button">+ Add New Reminder</button>
          </div>
        )}

        {activeTab === 'finance' && (
          <div className="finance-content">
            <button className="add-button">+ Add New Transaction</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default IndividualContact;
