// IndividualContact.js

import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const IndividualContact = () => {
  const { id } = useParams();
  const [contact, setContact] = useState(null);
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState({ text: "", dueDate: "", priority: "Low" });

  useEffect(() => {
    const fetchContact = async () => {
      try {
        const response = await axios.get(`/api/contacts/${id}`);
        setContact(response.data);
        setTodos(response.data.todos);
      } catch (error) {
        console.error("Error fetching contact:", error);
      }
    };
    fetchContact();
  }, [id]);

  const handleAddTodo = async () => {
    try {
      const response = await axios.post(`/api/contacts/${id}/todos`, newTodo);
      setTodos(response.data.todos);
      setNewTodo({ text: "", dueDate: "", priority: "Low" });
    } catch (error) {
      console.error("Error adding todo:", error);
    }
  };

  const handleUpdateTodo = async (todoId, updatedTodo) => {
    try {
      const response = await axios.put(`/api/contacts/${id}/todos/${todoId}`, updatedTodo);
      setTodos(response.data.todos);
    } catch (error) {
      console.error("Error updating todo:", error);
    }
  };

  const handleDeleteTodo = async (todoId) => {
    try {
      const response = await axios.delete(`/api/contacts/${id}/todos/${todoId}`);
      setTodos(response.data.todos);
    } catch (error) {
      console.error("Error deleting todo:", error);
    }
  };

  return (
    <div>
      {contact && (
        <div>
          <h2>{contact.name}</h2>
          <p>Phone: {contact.phone}</p>
          <p>Email: {contact.email}</p>
        </div>
      )}
      <div>
        <h3>Todos</h3>
        {todos.map((todo) => (
          <div key={todo._id}>
            <input
              type="checkbox"
              checked={todo.completed}
              onChange={() =>
                handleUpdateTodo(todo._id, { ...todo, completed: !todo.completed })
              }
            />
            <span>{todo.text}</span>
            <button onClick={() => handleDeleteTodo(todo._id)}>Delete</button>
          </div>
        ))}
        <div>
          <input
            type="text"
            value={newTodo.text}
            onChange={(e) => setNewTodo({ ...newTodo, text: e.target.value })}
            placeholder="Todo text"
          />
          <input
            type="date"
            value={newTodo.dueDate}
            onChange={(e) => setNewTodo({ ...newTodo, dueDate: e.target.value })}
          />
          <select
            value={newTodo.priority}
            onChange={(e) => setNewTodo({ ...newTodo, priority: e.target.value })}
          >
            <option value="Low">Low</option>
            <option value="Medium">Medium</option>
            <option value="High">High</option>
          </select>
          <button onClick={handleAddTodo}>Add Todo</button>
        </div>
      </div>
    </div>
  );
};

export default IndividualContact;
