import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './styles.css';
import { FaCheckSquare, FaBell, FaMoneyBillWave } from 'react-icons/fa';

const IndividualContact = () => {
  const { id } = useParams();
  const [activeTab, setActiveTab] = useState('todo');
  const [contact, setContact] = useState(null);
  const [newTodo, setNewTodo] = useState({ text: '', dueDate: '', priority: 'Medium' });
  const [filter, setFilter] = useState('all');
  const [sortOrder, setSortOrder] = useState('Low');

  useEffect(() => {
    // Fetch contact by ID
    const fetchContact = async () => {
      try {
        const response = await axios.get(`/api/contacts/${id}`);
        setContact(response.data);
      } catch (error) {
        console.error('Error fetching contact:', error);
      }
    };
    fetchContact();
  }, [id]);

  const handleAddTodo = async () => {
    try {
      const response = await axios.post(`/api/contacts/${id}/todos`, newTodo);
      setContact({ ...contact, todos: response.data });
      setNewTodo({ text: '', dueDate: '', priority: 'Medium' });
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  const handleToggleComplete = async (todoId) => {
    try {
      const response = await axios.put(`/api/contacts/${id}/todos/${todoId}`);
      setContact({ ...contact, todos: response.data });
    } catch (error) {
      console.error('Error toggling todo:', error);
    }
  };

  const handleEditTodo = async (todoId, updatedTodo) => {
    try {
      const response = await axios.put(`/api/contacts/${id}/todos/${todoId}/edit`, updatedTodo);
      setContact({ ...contact, todos: response.data });
    } catch (error) {
      console.error('Error editing todo:', error);
    }
  };

  const handleDeleteTodo = async (todoId) => {
    try {
      const response = await axios.delete(`/api/contacts/${id}/todos/${todoId}`);
      setContact({ ...contact, todos: response.data });
    } catch (error) {
      console.error('Error deleting todo:', error);
    }
  };

  const handleSortTodos = async () => {
    try {
      const response = await axios.get(`/api/contacts/${id}/todos/sort?order=${sortOrder}`);
      setContact({ ...contact, todos: response.data });
    } catch (error) {
      console.error('Error sorting todos:', error);
    }
  };

  const handleFilterTodos = async () => {
    try {
      const response = await axios.get(`/api/contacts/${id}/todos/filter?completed=${filter}`);
      setContact({ ...contact, todos: response.data });
    } catch (error) {
      console.error('Error filtering todos:', error);
    }
  };

  return (
    <div className="individual-contact">
      <header className="header">
        <button className="back-button" onClick={() => window.history.back()}>← Contacts</button>
      </header>
      <div className="contact-summary">
        <div className="contact-photo">
          <i className="bi bi-person-circle"></i>
        </div>
        <h2>{contact?.name || "Contact"}</h2>
      </div>

      <div className="tab-bar">
        <button
          className={`tab ${activeTab === 'todo' ? 'active' : ''}`}
          onClick={() => setActiveTab('todo')}
        >
          <FaCheckSquare className="icon" /> Todo
        </button>
        <button
          className={`tab ${activeTab === 'reminder' ? 'active' : ''}`}
          onClick={() => setActiveTab('reminder')}
        >
          <FaBell className="icon" /> Reminder
        </button>
        <button
          className={`tab ${activeTab === 'finance' ? 'active' : ''}`}
          onClick={() => setActiveTab('finance')}
        >
          <FaMoneyBillWave className="icon" /> Finance
        </button>
      </div>

      <div className="tab-content">
        {activeTab === 'todo' && contact && (
          <div className="todo-content">
            <div className="todo-filters">
              <select onChange={(e) => setSortOrder(e.target.value)} value={sortOrder}>
                <option value="Low">Low Priority</option>
                <option value="Medium">Medium Priority</option>
                <option value="High">High Priority</option>
              </select>
              <button onClick={handleSortTodos}>Sort</button>

              <select onChange={(e) => setFilter(e.target.value)} value={filter}>
                <option value="all">All</option>
                <option value="true">Completed</option>
                <option value="false">Pending</option>
              </select>
              <button onClick={handleFilterTodos}>Filter</button>
            </div>
            
            {contact.todos.map((todo) => (
              <div key={todo._id} className="list-item">
                <span>{todo.text}</span>
                <button onClick={() => handleToggleComplete(todo._id)}>
                  {todo.completed ? 'Unmark' : 'Mark as Completed'}
                </button>
                <button onClick={() => handleDeleteTodo(todo._id)}>Delete</button>
              </div>
            ))}

            <div className="todo-form">
              <input
                type="text"
                value={newTodo.text}
                onChange={(e) => setNewTodo({ ...newTodo, text: e.target.value })}
                placeholder="New Todo"
              />
              <button onClick={handleAddTodo}>Add Todo</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default IndividualContact;
