import React, { useEffect, useState } from 'react';
import axios from 'axios';

const IndividualContact = ({ match }) => {
  const [contact, setContact] = useState(null);
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState({
    text: '',
    dueDate: '',
    priority: 'Low',
  });
  const [editingTodoId, setEditingTodoId] = useState(null);
  const [editTodo, setEditTodo] = useState({
    text: '',
    dueDate: '',
    priority: 'Low',
    completed: false,
  });

  const contactId = match.params.contactId;

  // Fetch contact and todos
  useEffect(() => {
    const fetchContact = async () => {
      try {
        const contactResponse = await axios.get(`/api/contacts/${contactId}`);
        setContact(contactResponse.data);
      } catch (error) {
        console.error('Error fetching contact details:', error);
      }
    };

    const fetchTodos = async () => {
      try {
        const todosResponse = await axios.get(`/api/contacts/${contactId}/todos`);
        setTodos(todosResponse.data);
      } catch (error) {
        console.error('Error fetching todos:', error);
      }
    };

    fetchContact();
    fetchTodos();
  }, [contactId]);

  // Handle new todo input
  const handleNewTodoChange = (e) => {
    const { name, value } = e.target;
    setNewTodo((prev) => ({ ...prev, [name]: value }));
  };

  // Add a new todo
  const handleAddTodo = async () => {
    try {
      const response = await axios.post(`/api/contacts/${contactId}/todos`, newTodo);
      setTodos([...todos, response.data]);
      setNewTodo({ text: '', dueDate: '', priority: 'Low' });
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  // Edit an existing todo
  const handleEditTodoChange = (e) => {
    const { name, value } = e.target;
    setEditTodo((prev) => ({ ...prev, [name]: value }));
  };

  const handleStartEdit = (todo) => {
    setEditingTodoId(todo._id);
    setEditTodo({
      text: todo.text,
      dueDate: todo.dueDate ? new Date(todo.dueDate).toISOString().split('T')[0] : '',
      priority: todo.priority,
      completed: todo.completed,
    });
  };

  const handleUpdateTodo = async () => {
    try {
      const response = await axios.put(`/api/todos/${editingTodoId}`, editTodo);
      setTodos(todos.map((todo) => (todo._id === editingTodoId ? response.data : todo)));
      setEditingTodoId(null);
      setEditTodo({ text: '', dueDate: '', priority: 'Low', completed: false });
    } catch (error) {
      console.error('Error updating todo:', error);
    }
  };

  // Delete a todo
  const handleDeleteTodo = async (todoId) => {
    try {
      await axios.delete(`/api/todos/${todoId}`);
      setTodos(todos.filter((todo) => todo._id !== todoId));
    } catch (error) {
      console.error('Error deleting todo:', error);
    }
  };

  return (
    <div>
      {contact ? (
        <div>
          <h2>{contact.name}</h2>
          <p>Email: {contact.email}</p>
          <p>Phone: {contact.phone}</p>

          {/* Add New Todo */}
          <div>
            <h3>Add New Todo</h3>
            <input
              type="text"
              name="text"
              value={newTodo.text}
              onChange={handleNewTodoChange}
              placeholder="Todo text"
            />
            <input
              type="date"
              name="dueDate"
              value={newTodo.dueDate}
              onChange={handleNewTodoChange}
            />
            <select
              name="priority"
              value={newTodo.priority}
              onChange={handleNewTodoChange}
            >
              <option value="Low">Low</option>
              <option value="Medium">Medium</option>
              <option value="High">High</option>
            </select>
            <button onClick={handleAddTodo}>Add Todo</button>
          </div>

          {/* Todo List */}
          <div>
            <h3>Todo List</h3>
            <ul>
              {todos.map((todo) => (
                <li key={todo._id}>
                  {editingTodoId === todo._id ? (
                    // Edit mode
                    <div>
                      <input
                        type="text"
                        name="text"
                        value={editTodo.text}
                        onChange={handleEditTodoChange}
                      />
                      <input
                        type="date"
                        name="dueDate"
                        value={editTodo.dueDate}
                        onChange={handleEditTodoChange}
                      />
                      <select
                        name="priority"
                        value={editTodo.priority}
                        onChange={handleEditTodoChange}
                      >
                        <option value="Low">Low</option>
                        <option value="Medium">Medium</option>
                        <option value="High">High</option>
                      </select>
                      <input
                        type="checkbox"
                        name="completed"
                        checked={editTodo.completed}
                        onChange={() =>
                          setEditTodo((prev) => ({
                            ...prev,
                            completed: !prev.completed,
                          }))
                        }
                      />
                      <button onClick={handleUpdateTodo}>Update</button>
                      <button onClick={() => setEditingTodoId(null)}>
                        Cancel
                      </button>
                    </div>
                  ) : (
                    // View mode
                    <div>
                      <input
                        type="checkbox"
                        checked={todo.completed}
                        onChange={() =>
                          handleStartEdit({
                            ...todo,
                            completed: !todo.completed,
                          })
                        }
                      />
                      <span>{todo.text}</span>
                      <span>Due: {todo.dueDate ? new Date(todo.dueDate).toLocaleDateString() : 'No due date'}</span>
                      <span>Priority: {todo.priority}</span>
                      <button onClick={() => handleStartEdit(todo)}>Edit</button>
                      <button onClick={() => handleDeleteTodo(todo._id)}>Delete</button>
                    </div>
                  )}
                </li>
              ))}
            </ul>
          </div>
        </div>
      ) : (
        <p>Loading contact details...</p>
      )}
    </div>
  );
};

export default IndividualContact;
