// src/IndividualContact.js
import React, { useState } from 'react';
import { useLocation, Link } from 'react-router-dom';
import './styles.css';
import { FaCheckSquare, FaBell, FaMoneyBillWave } from 'react-icons/fa';
import Contacts from './Contacts';

const IndividualContact = () => {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const name = params.get('name'); // Get the contact name from URL
  const avatar = params.get('avatar'); // Get the contact avatar

  const [activeTab, setActiveTab] = useState('todo'); // State to track active tab
  const [todos, setTodos] = useState([{ text: "Complete Today's Task" }, { text: "Schedule meeting" }]); // Sample todo data
  const [reminders, setReminders] = useState([]); // Sample reminder data
  const [transactions, setTransactions] = useState([]); // Sample transaction data

  return (
    <div className="individual-contact">
      <header className="header">
        {/* <button className="back-button">← Contacts</button> */}
        <button className="back-button" onClick={() => window.history.back()}>← Contacts</button>

        {/* <h1 className="title">Contact</h1> */}
      </header>
      <div className="contact-summary">
        <div className="contact-photo">
          <i className="bi bi-person-circle"></i>
        </div>
        <h2>{name || "Contact"}</h2>
      </div>
      <div className="tab-bar">
        <button
          className={`tab ${activeTab === 'todo' ? 'active' : ''}`}
          onClick={() => setActiveTab('todo')}
        >
          <FaCheckSquare className="icon" /> Todo
        </button>
        <button
          className={`tab ${activeTab === 'reminder' ? 'active' : ''}`}
          onClick={() => setActiveTab('reminder')}
        >
          <FaBell className="icon" /> Reminder
        </button>
        <button
          className={`tab ${activeTab === 'finance' ? 'active' : ''}`}
          onClick={() => setActiveTab('finance')}
        >
          <FaMoneyBillWave className="icon" /> Finance
        </button>
      </div>
      <div className="tab-content">
        {activeTab === 'todo' && (
          <div className="todo-content">
            {todos.map((todo, index) => (
              <div key={index} className="list-item">
                <span>{todo.text}</span>
                <FaCheckSquare className="status-icon" />
              </div>
            ))}
            <button className="add-button">+ Add New Todo</button>
          </div>
        )}
        {activeTab === 'reminder' && (
          <div className="reminder-content">
            <button className="add-button">+ Add New Reminder</button>
          </div>
        )}
        {activeTab === 'finance' && (
          <div className="finance-content">
            <button className="add-button">+ Add New Transaction</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default IndividualContact;
