import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Contacts = () => {
  const [contacts, setContacts] = useState([]);
  const [newContact, setNewContact] = useState({ name: '', avatar: '', category: '' });
  const category = 'YourCategoryName'; // Replace this with the actual category you want to fetch

  useEffect(() => {
    fetchContacts();
  }, []);

  const fetchContacts = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/contacts/${category}`);
      setContacts(response.data);
    } catch (error) {
      console.error('Error fetching contacts:', error);
    }
  };

  const handleAddContact = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/contacts', newContact);
      setNewContact({ name: '', avatar: '', category: '' }); // Reset input fields
      fetchContacts(); // Fetch contacts again to update the UI
    } catch (error) {
      console.error('Error adding contact:', error);
    }
  };

  return (
    <div>
      <h1>Contacts</h1>
      <form onSubmit={handleAddContact}>
        <input
          type="text"
          placeholder="Name"
          value={newContact.name}
          onChange={(e) => setNewContact({ ...newContact, name: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Avatar URL"
          value={newContact.avatar}
          onChange={(e) => setNewContact({ ...newContact, avatar: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Category"
          value={newContact.category}
          onChange={(e) => setNewContact({ ...newContact, category: e.target.value })}
          required
        />
        <button type="submit">Add Contact</button>
      </form>
      <div>
        {contacts.map((contact) => (
          <div key={contact._id}>
            <h2>{contact.name}</h2>
            <img src={contact.avatar} alt={contact.name} />
            <p>Category: {contact.category}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Contacts;
