import React, { useState, useEffect } from 'react';
import axios from 'axios';

function IndividualContact({ contactId }) {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState({
    title: '',
    dueDate: '',
    priority: 'Low',
    completed: false,
  });
  const [filter, setFilter] = useState('All');
  const [sortOption, setSortOption] = useState('Date');

  // Fetch contact's todos on load
  useEffect(() => {
    const fetchTodos = async () => {
      try {
        const response = await axios.get(`/api/contacts/${contactId}/todos`);
        setTodos(response.data);
      } catch (error) {
        console.error('Error fetching todos:', error);
      }
    };
    fetchTodos();
  }, [contactId]);

  // Add new todo
  const addTodo = async () => {
    try {
      const response = await axios.post(`/api/contacts/${contactId}/todos`, newTodo);
      setTodos([...todos, response.data]);
      setNewTodo({ title: '', dueDate: '', priority: 'Low', completed: false });
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  // Update todo
  const updateTodo = async (id, updatedFields) => {
    try {
      const response = await axios.put(`/api/contacts/${contactId}/todos/${id}`, updatedFields);
      setTodos(todos.map(todo => todo._id === id ? response.data : todo));
    } catch (error) {
      console.error('Error updating todo:', error);
    }
  };

  // Delete todo
  const deleteTodo = async (id) => {
    try {
      await axios.delete(`/api/contacts/${contactId}/todos/${id}`);
      setTodos(todos.filter(todo => todo._id !== id));
    } catch (error) {
      console.error('Error deleting todo:', error);
    }
  };

  // Mark todo as complete/incomplete
  const toggleComplete = async (id, completed) => {
    await updateTodo(id, { completed: !completed });
  };

  // Sorting function
  const sortedTodos = todos.slice().sort((a, b) => {
    if (sortOption === 'Date') {
      return new Date(a.dueDate) - new Date(b.dueDate);
    } else if (sortOption === 'Priority') {
      const priorities = { High: 1, Medium: 2, Low: 3 };
      return priorities[a.priority] - priorities[b.priority];
    }
  });

  // Filtering function
  const filteredTodos = sortedTodos.filter(todo => {
    if (filter === 'All') return true;
    if (filter === 'Completed') return todo.completed;
    if (filter === 'Incomplete') return !todo.completed;
  });

  return (
    <div>
      <h2>Todo List</h2>
      <div>
        <input
          type="text"
          placeholder="Todo Title"
          value={newTodo.title}
          onChange={(e) => setNewTodo({ ...newTodo, title: e.target.value })}
        />
        <input
          type="date"
          value={newTodo.dueDate}
          onChange={(e) => setNewTodo({ ...newTodo, dueDate: e.target.value })}
        />
        <select
          value={newTodo.priority}
          onChange={(e) => setNewTodo({ ...newTodo, priority: e.target.value })}
        >
          <option value="Low">Low</option>
          <option value="Medium">Medium</option>
          <option value="High">High</option>
        </select>
        <button onClick={addTodo}>Add Todo</button>
      </div>
      <div>
        <label>Filter: </label>
        <select value={filter} onChange={(e) => setFilter(e.target.value)}>
          <option value="All">All</option>
          <option value="Completed">Completed</option>
          <option value="Incomplete">Incomplete</option>
        </select>
        <label>Sort by: </label>
        <select value={sortOption} onChange={(e) => setSortOption(e.target.value)}>
          <option value="Date">Due Date</option>
          <option value="Priority">Priority</option>
        </select>
      </div>
      <ul>
        {filteredTodos.map(todo => (
          <li key={todo._id} style={{ textDecoration: todo.completed ? 'line-through' : 'none' }}>
            <input
              type="checkbox"
              checked={todo.completed}
              onChange={() => toggleComplete(todo._id, todo.completed)}
            />
            {todo.title} - {todo.priority} - Due: {new Date(todo.dueDate).toLocaleDateString()}
            <button onClick={() => updateTodo(todo._id, { ...todo, title: prompt('New title:', todo.title) })}>
              Edit
            </button>
            <button onClick={() => deleteTodo(todo._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default IndividualContact;
