// src/components/IndividualContact.js
import React, { useState, useEffect } from 'react';
import { useLocation, useParams } from 'react-router-dom';
import axios from 'axios';
import { FaCheckSquare, FaBell, FaMoneyBillWave, FaTrash, FaEdit } from 'react-icons/fa';
import './styles.css';

const API_BASE_URL = 'http://localhost:5000/api';

// TodoForm Component
const TodoForm = ({ onSubmit, initialData = null, onCancel }) => {
  const [todo, setTodo] = useState({
    text: initialData?.text || '',
    dueDate: initialData?.dueDate ? new Date(initialData.dueDate).toISOString().split('T')[0] : '',
    priority: initialData?.priority || 'medium'
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(todo);
  };

  return (
    <form onSubmit={handleSubmit} className="todo-form">
      <input
        type="text"
        value={todo.text}
        onChange={(e) => setTodo({ ...todo, text: e.target.value })}
        placeholder="Todo text"
        required
        className="todo-input"
      />
      <input
        type="date"
        value={todo.dueDate}
        onChange={(e) => setTodo({ ...todo, dueDate: e.target.value })}
        className="todo-input"
      />
      <select
        value={todo.priority}
        onChange={(e) => setTodo({ ...todo, priority: e.target.value })}
        className="todo-input"
      >
        <option value="low">Low Priority</option>
        <option value="medium">Medium Priority</option>
        <option value="high">High Priority</option>
      </select>
      <div className="todo-form-actions">
        <button type="submit" className="todo-submit">
          {initialData ? 'Update Todo' : 'Add Todo'}
        </button>
        {onCancel && (
          <button type="button" className="todo-cancel" onClick={onCancel}>
            Cancel
          </button>
        )}
      </div>
    </form>
  );
};

// Main IndividualContact Component
const IndividualContact = () => {
  const location = useLocation();
  const { id: contactId } = useParams();
  const [contact, setContact] = useState(null);
  const [activeTab, setActiveTab] = useState('todo');
  const [todos, setTodos] = useState([]);
  const [showTodoForm, setShowTodoForm] = useState(false);
  const [editingTodo, setEditingTodo] = useState(null);
  const [sortBy, setSortBy] = useState('dueDate');
  const [filterPriority, setFilterPriority] = useState('all');
  const [reminders, setReminders] = useState([]);
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    fetchContact();
    fetchTodos();
  }, [contactId]);

  const fetchContact = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/contacts/${contactId}`);
      setContact(response.data);
      setReminders(response.data.reminders || []);
      setTransactions(response.data.transactions || []);
    } catch (error) {
      console.error('Error fetching contact:', error);
    }
  };

  const fetchTodos = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/contacts/${contactId}/todos`);
      setTodos(response.data);
    } catch (error) {
      console.error('Error fetching todos:', error);
    }
  };

  const handleAddTodo = async (todoData) => {
    try {
      const response = await axios.post(`${API_BASE_URL}/contacts/${contactId}/todos`, todoData);
      setTodos([...todos, response.data]);
      setShowTodoForm(false);
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  const handleUpdateTodo = async (todoId, todoData) => {
    try {
      const response = await axios.put(`${API_BASE_URL}/contacts/${contactId}/todos/${todoId}`, todoData);
      setTodos(todos.map(todo => todo._id === todoId ? response.data : todo));
      setEditingTodo(null);
    } catch (error) {
      console.error('Error updating todo:', error);
    }
  };

  const handleDeleteTodo = async (todoId) => {
    try {
      await axios.delete(`${API_BASE_URL}/contacts/${contactId}/todos/${todoId}`);
      setTodos(todos.filter(todo => todo._id !== todoId));
    } catch (error) {
      console.error('Error deleting todo:', error);
    }
  };

  const handleToggleComplete = async (todoId, completed) => {
    try {
      await handleUpdateTodo(todoId, { completed: !completed });
    } catch (error) {
      console.error('Error toggling todo completion:', error);
    }
  };

  const getSortedAndFilteredTodos = () => {
    let filteredTodos = todos;
    
    // Apply priority filter
    if (filterPriority !== 'all') {
      filteredTodos = filteredTodos.filter(todo => todo.priority === filterPriority);
    }
    
    // Apply sorting
    return filteredTodos.sort((a, b) => {
      if (sortBy === 'dueDate') {
        return new Date(a.dueDate) - new Date(b.dueDate);
      } else if (sortBy === 'priority') {
        const priorityOrder = { high: 0, medium: 1, low: 2 };
        return priorityOrder[a.priority] - priorityOrder[b.priority];
      }
      return 0;
    });
  };

  return (
    <div className="individual-contact">
      <header className="header">
        <button className="back-button" onClick={() => window.history.back()}>
          ← Contacts
        </button>
      </header>

      {contact && (
        <div className="contact-summary">
          <div className="contact-photo">
            {contact.avatar ? (
              <img src={contact.avatar} alt={contact.name} className="contact-avatar" />
            ) : (
              <div className="contact-avatar-placeholder">
                {contact.name.charAt(0)}
              </div>
            )}
          </div>
          <h2>{contact.name}</h2>
          {contact.category && <span className="contact-category">{contact.category}</span>}
        </div>
      )}
      
      <div className="tab-bar">
        <button
          className={`tab ${activeTab === 'todo' ? 'active' : ''}`}
          onClick={() => setActiveTab('todo')}
        >
          <FaCheckSquare className="icon" /> Todo
        </button>
        <button
          className={`tab ${activeTab === 'reminder' ? 'active' : ''}`}
          onClick={() => setActiveTab('reminder')}
        >
          <FaBell className="icon" /> Reminder
        </button>
        <button
          className={`tab ${activeTab === 'finance' ? 'active' : ''}`}
          onClick={() => setActiveTab('finance')}
        >
          <FaMoneyBillWave className="icon" /> Finance
        </button>
      </div>

      {activeTab === 'todo' && (
        <div className="todo-content">
          <div className="todo-controls">
            <select 
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="todo-sort"
            >
              <option value="dueDate">Sort by Due Date</option>
              <option value="priority">Sort by Priority</option>
            </select>
            
            <select
              value={filterPriority}
              onChange={(e) => setFilterPriority(e.target.value)}
              className="todo-filter"
            >
              <option value="all">All Priorities</option>
              <option value="high">High Priority</option>
              <option value="medium">Medium Priority</option>
              <option value="low">Low Priority</option>
            </select>
          </div>

          {showTodoForm && !editingTodo && (
            <TodoForm 
              onSubmit={handleAddTodo}
              onCancel={() => setShowTodoForm(false)}
            />
          )}

          {getSortedAndFilteredTodos().map((todo) => (
            <div key={todo._id} className={`todo-item priority-${todo.priority}`}>
              {editingTodo?._id === todo._id ? (
                <TodoForm 
                  onSubmit={(todoData) => handleUpdateTodo(todo._id, todoData)} 
                  initialData={todo}
                  onCancel={() => setEditingTodo(null)}
                />
              ) : (
                <>
                  <div className="todo-checkbox">
                    <input
                      type="checkbox"
                      checked={todo.completed}
                      onChange={() => handleToggleComplete(todo._id, todo.completed)}
                    />
                  </div>
                  <div className="todo-details">
                    <span className={`todo-text ${todo.completed ? 'completed' : ''}`}>
                      {todo.text}
                    </span>
                    <span className="todo-due-date">
                      Due: {new Date(todo.dueDate).toLocaleDateString()}
                    </span>
                    <span className={`todo-priority priority-${todo.priority}`}>
                      {todo.priority}
                    </span>
                  </div>
                  <div className="todo-actions">
                    <FaEdit 
                      className="todo-edit"
                      onClick={() => setEditingTodo(todo)}
                    />
                    <FaTrash 
                      className="todo-delete"
                      onClick={() => handleDeleteTodo(todo._id)}
                    />
                  </div>
                </>
              )}
            </div>
          ))}

          {!showTodoForm && (
            <button 
              className="add-button"
              onClick={() => setShowTodoForm(true)}
            >
              + Add New Todo
            </button>
          )}
        </div>
      )}

      {activeTab === 'reminder' && (
        <div className="reminder-content">
          {/* Reminder content will be implemented similarly */}
          <button className="add-button">+ Add New Reminder</button>
        </div>
      )}

      {activeTab === 'finance' && (
        <div className="finance-content">
          {/* Finance content will be implemented similarly */}
          <button className="add-button">+ Add New Transaction</button>
        </div>
      )}
    </div>
  );
};

export default IndividualContact;
