import React, { useEffect, useState } from 'react';
import { useLocation, Link } from 'react-router-dom';
import axios from 'axios';
import './styles.css';

function Contacts() {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const category = params.get('category');
  const [contacts, setContacts] = useState([]);
  const [newContactName, setNewContactName] = useState('');
  const [newContactAvatar, setNewContactAvatar] = useState('');
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/contacts');
        setContacts(response.data);
      } catch (error) {
        console.error('Error fetching contacts:', error);
      }
    };

    fetchContacts();
  }, []);

  const handleAddContact = async () => {
    try {
      const newContact = { name: newContactName, avatar: newContactAvatar };
      const response = await axios.post('http://localhost:5000/api/contacts', newContact);
      setContacts([...contacts, response.data]); // Update contacts state
      setNewContactName('');
      setNewContactAvatar('');
      setShowForm(false); // Hide the form after submission
    } catch (error) {
      console.error('Error adding contact:', error);
    }
  };

  return (
    <>
      <button onClick={() => setShowForm(!showForm)}>
        {showForm ? 'Cancel' : 'Create New Contact'}
      </button>

      {showForm && (
        <div className="new-contact-form">
          <input
            type="text"
            placeholder="Contact Name"
            value={newContactName}
            onChange={(e) => setNewContactName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Avatar URL"
            value={newContactAvatar}
            onChange={(e) => setNewContactAvatar(e.target.value)}
          />
          <button onClick={handleAddContact}>Save Contact</button>
        </div>
      )}

      <div className="container">
        {contacts.length > 0 ? (
          contacts.map((contact, index) => (
            <Link
              key={index}
              className="card"
              to={`/contact?name=${contact.name}&avatar=${contact.avatar}`}
            >
              <div className="avatar">{contact.avatar}</div>
              <p className="name">{contact.name}</p>
            </Link>
          ))
        ) : (
          <p className="no-contacts">No contacts found for this category.</p>
        )}
      </div>
    </>
  );
}

export default Contacts;









