// src/IndividualContact.js
import React, { useState, useEffect } from 'react';
import { useLocation, useHistory } from 'react-router-dom';
import axios from 'axios';
import './styles.css';
import TodoTab from './TodoTab';

const IndividualContact = () => {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const contactId = params.get('contactId'); // Get the contactId from URL
  const name = params.get('name'); // Get the contact name
  const avatar = params.get('avatar'); // Get the contact avatar

  const [activeTab, setActiveTab] = useState('todo');

  useEffect(() => {
    if (!contactId) {
      console.error('Contact ID is missing');
    }
  }, [contactId]);

  return (
    <div className="individual-contact">
      <header className="header">
        <button className="back-button" onClick={() => window.history.back()}>← Contacts</button>
      </header>
      <div className="contact-summary">
        <div className="contact-photo">
          <i className="bi bi-person-circle"></i>
        </div>
        <h2>{name || "Contact"}</h2>
      </div>
      <div className="tab-bar">
        <button
          className={`tab ${activeTab === 'todo' ? 'active' : ''}`}
          onClick={() => setActiveTab('todo')}
        >
          Todo
        </button>
      </div>
      <div className="tab-content">
        {activeTab === 'todo' && contactId && (
          <TodoTab contactId={contactId} />
        )}
      </div>
    </div>
  );
};

export default IndividualContact;
