import React, { useState, useEffect } from 'react';
import { useLocation, Link } from 'react-router-dom';
import './styles.css';
import axios from 'axios';

const Contacts = () => {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const category = params.get('category');

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newContact, setNewContact] = useState({
    name: '',
    avatar: '',
    email: '',
    contact: ''
  });
  const [contacts, setContacts] = useState([]);

  // Fetch contacts from the server on load and whenever category changes
  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/contacts?category=${category}`);
        setContacts(response.data);
      } catch (error) {
        console.error('Error fetching contacts:', error);
      }
    };
    fetchContacts();
  }, [category]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewContact({ ...newContact, [name]: value });
  };

  const handleAddContact = () => {
    if (!newContact.name || !newContact.avatar) {
      alert('Please fill in the required fields.');
      return;
    }

    axios.post('http://localhost:5000/api/contacts', newContact)
      .then(response => {
        setContacts([...contacts, response.data]); // Update contacts with newly added contact
        setIsModalOpen(false);
      })
      .catch(error => {
        console.error('There was an error adding the contact!', error);
      });
  };

  return (
    <>
      <header className="header">
        <div className="logo">PeoplePixel</div>
        <input type="text" placeholder="Search..." className="search-bar" />
        <div className="profile-icon">
          <i className="bi bi-person-circle"></i>
        </div>
      </header>
      <h2>
        <button className="back-button" onClick={() => window.history.back()}>‚Üê Contacts</button>
      </h2>
      <h3>
        <button className="add-contact" onClick={() => setIsModalOpen(true)}>
          Add Contact
        </button>
      </h3>
      <div className="container">
        {contacts.length > 0 ? (
          contacts.map((contact, index) => (
            <Link
              key={index}
              className="card"
              to={`/contact?name=${contact.name}&avatar=${contact.avatar}`}
            >
              <div className="avatar">{contact.avatar}</div>
              <p className="name">{contact.name}</p>
            </Link>
          ))
        ) : (
          <p className="no-contacts">No contacts found for this category.</p>
        )}
      </div>

      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>Add New Contact</h2>
            <form>
              <label>Name:</label>
              <input type="text" name="name" value={newContact.name} onChange={handleInputChange} required />
              <label>Avatar:</label>
              <input type="text" name="avatar" value={newContact.avatar} onChange={handleInputChange} required />
              <label>Email:</label>
              <input type="email" name="email" value={newContact.email} onChange={handleInputChange} />
              <label>Contact:</label>
              <input type="text" name="contact" value={newContact.contact} onChange={handleInputChange} />
            </form>
            <button onClick={handleAddContact}>Save</button>
            <button onClick={() => setIsModalOpen(false)}>Cancel</button>
          </div>
        </div>
      )}

      <footer>
        <div className="footer-links">
          <a href="#">About</a>
          <a href="#">Help</a>
          <a href="#">Privacy Policy</a>
        </div>
        <p className="copyright">&copy; 2024 PeoplePixel. All rights reserved.</p>
      </footer>
    </>
  );
};

export default Contacts;
