// src/IndividualContact.js
import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { fetchTodos, addTodo } from './api';
import './styles.css';
import { FaCheckSquare, FaPlusCircle } from 'react-icons/fa';

const IndividualContact = () => {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const contactId = params.get('contactId');
  const name = params.get('name');
  const avatar = params.get('avatar');

  const [todos, setTodos] = useState([]);
  const [todoText, setTodoText] = useState('');
  const [todoPriority, setTodoPriority] = useState('Medium');
  const [todoDueDate, setTodoDueDate] = useState('');

  // Fetch todos for the contact when component mounts
  useEffect(() => {
    const loadTodos = async () => {
      try {
        const todosData = await fetchTodos(contactId);
        setTodos(todosData);
      } catch (error) {
        console.error('Error fetching todos from individual:', error);
      }
    };
    loadTodos();
  }, [contactId]);

 

  // Handle adding a new todo
  const handleAddTodo = async () => {
    if (!todoText.trim()) return;

    const newTodo = {
      text: todoText,
      priority: todoPriority,
      dueDate: todoDueDate,
    };
  
    try {
      const addedTodo = await addTodo(contactId, newTodo);
      setTodos([...todos, addedTodo]);  // Add to the list
      setTodoText('');                  // Clear the input fields
      setTodoPriority('Medium');
      setTodoDueDate('');
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  return (
    <div className="individual-contact">
      <header className="header">
        <button className="back-button" onClick={() => window.history.back()}>← Contacts</button>
      </header>

      <div className="contact-summary">
        <div className="contact-photo">
          <img src={avatar || '/default-avatar.jpg'} alt={`${name}'s avatar`} />
        </div>
        <h2>{name || "Contact"}</h2>
      </div>

      <div className="todo-content">
        <div className="todo-header">
          <button className="add-button" onClick={handleAddTodo}>
            <FaPlusCircle className="icon" /> Add New Todo
          </button>
        </div>
        
        <div className="todo-input">
          <input
            type="text"
            placeholder="Todo text"
            value={todoText}
            onChange={(e) => setTodoText(e.target.value)}
          />
          <input
            type="date"
            value={todoDueDate}
            onChange={(e) => setTodoDueDate(e.target.value)}
          />
          <select
            value={todoPriority}
            onChange={(e) => setTodoPriority(e.target.value)}
          >
            <option value="High">High</option>
            <option value="Medium">Medium</option>
            <option value="Low">Low</option>
          </select>
        </div>

        <div className="todo-list">
          {todos.length === 0 ? (
            <p>No todos found</p>
          ) : (
            todos.map(todo => (
              <div key={todo._id} className={`list-item ${todo.completed ? 'completed' : ''}`}>
                <span>{todo.text}</span> - <span>{todo.priority}</span>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default IndividualContact;
