//src/IndividualContact.js
import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { fetchTodos, addTodo, toggleTodo } from './api';
import './styles.css';
import { FaCheckSquare, FaBell, FaMoneyBillWave, FaPlusCircle } from 'react-icons/fa';

const IndividualContact = () => {
  debugger;
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const name = params.get('name');
  const avatar = params.get('avatar');
  const contactId = params.get('contactId');

  const [activeTab, setActiveTab] = useState('todo');
  const [todos, setTodos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch todos when component mounts or contactId changes
  useEffect(() => {
    debugger;
    const loadTodos = async () => {
      if (!contactId) {
        setError('No contact ID provided');
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        const todoData = await fetchTodos(contactId);
        setTodos(todoData);
        setError(null);
      } catch (err) {
        setError('Failed to load todos: ' + err.message);
      } finally {
        setLoading(false);
      }
    };

    loadTodos();
  }, [contactId]);

  // Handle adding a new todo
  const handleAddTodo = async (todoText) => {
    debugger;
    if (!todoText.trim())
    {
      alert(todoText)
       return;
    }
  
    try {
      const newTodo = {
        text: todoText,
        completed: false,
        priority: 'Medium'
      };
  
      console.log('Adding new todo:', newTodo);  // Log the todo data before API call
      //const addedTodo = await addTodo(contactId, newTodo);

      debugger;
      const response = await axios.post('http://localhost:5000/api/todos', newTodo);
      setTodos([...todos, response.data]); // Add new category to state
      //setNewCategory('');
      //setShowModal(false);
      //setContacts([...contacts, response.data]);
      //setIsModalOpen(false);

      //console.log('Added todo:', addedTodo);  // Log the response from API
      setTodos(prevTodos => [...prevTodos, newTodo]);
    } catch (err) {
      setError('Failed to add todo: ' + err.message);
      console.error('Error in handleAddTodo:', err);  // Log the error
    }
  };
  

  // Handle toggling todo completion
  const handleToggleTodo = async (todoId, completed) => {
    try {
      const updatedTodo = await toggleTodo(todoId, !completed);
      setTodos(prevTodos =>
        prevTodos.map(todo =>
          todo._id === todoId ? updatedTodo : todo
        )
      );
    } catch (err) {
      setError('Failed to update todo: ' + err.message);
    }
  };

  return (
    <div className="individual-contact">
      <header className="header">
        <button className="back-button" onClick={() => window.history.back()}>← Contacts</button>
      </header>

      <div className="contact-summary">
        <div className="contact-photo">
          <img src={avatar || '/default-avatar.jpg'} alt={`${name}'s avatar`} />
        </div>
        <h2>{name || "Contact"}</h2>
      </div>

      <div className="tab-bar">
        <button
          className={`tab ${activeTab === 'todo' ? 'active' : ''}`}
          onClick={() => setActiveTab('todo')}
        >
          <FaCheckSquare className="icon" /> Todo
        </button>
        <button
          className={`tab ${activeTab === 'reminder' ? 'active' : ''}`}
          onClick={() => setActiveTab('reminder')}
        >
          <FaBell className="icon" /> Reminder
        </button>
        <button
          className={`tab ${activeTab === 'finance' ? 'active' : ''}`}
          onClick={() => setActiveTab('finance')}
        >
          <FaMoneyBillWave className="icon" /> Finance
        </button>
      </div>

      <div className="tab-content">
        {activeTab === 'todo' && (
          <div className="todo-content">
            {error && <div className="error-message">{error}</div>}
            
            <div className="todo-header">
              <text
                className="add-button" id = 'txttodo'
                onClick={() => {
                  const newTodoText = prompt('Enter new todo:');
                  if (newTodoText) handleAddTodo(newTodoText);
                }}
              >
                <FaPlusCircle className="icon" /> Add New Todo
              </text>
            </div>

            <div className="todo-list">
              {loading ? (
                <p>Loading todos...</p>
              ) : todos.length === 0 ? (
                <p>No todos found</p>
              ) : (
                todos.map(todo => (
                  <div key={todo._id} className={`list-item ${todo.completed ? 'completed' : ''}`}>
                    <input
                      type="checkbox"
                      checked={todo.completed}
                      onChange={() => handleToggleTodo(todo._id, todo.completed)}
                    />
                    <span>{todo.text}</span>
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {activeTab === 'reminder' && (
          <div className="reminder-content">
            <button className="add-button">+ Add New Reminder</button>
          </div>
        )}

        {activeTab === 'finance' && (
          <div className="finance-content">
            <button className="add-button">+ Add New Transaction</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default IndividualContact;
