// src/IndividualContact.js
import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './styles.css';
import { FaCheckSquare, FaBell, FaMoneyBillWave } from 'react-icons/fa';
// import { getContactById, updateContact, deleteContact } from '../api';
import { getContactById, updateContact, deleteContact } from './api'; // relative to src/

const IndividualContact = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const params = new URLSearchParams(location.search);
  const contactId = params.get('id'); // Assuming ID is passed in URL as 'id'

  const [contact, setContact] = useState(null);
  const [activeTab, setActiveTab] = useState('todo');
  const [todos, setTodos] = useState([]);
  const [reminders, setReminders] = useState([]);
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    // Fetch the contact data by ID
    const fetchContact = async () => {
      try {
        const data = await getContactById(contactId);
        setContact(data);
        setTodos(data.todos || []);
        setReminders(data.reminders || []);
        setTransactions(data.transactions || []);
      } catch (error) {
        console.error('Error fetching contact data:', error);
      }
    };
    fetchContact();
  }, [contactId]);

  const handleAddTodo = () => {
    const newTodo = prompt('Enter new todo:');
    if (newTodo) {
      const updatedTodos = [...todos, { text: newTodo }];
      setTodos(updatedTodos);
      updateContact(contactId, { ...contact, todos: updatedTodos });
    }
  };

  const handleAddReminder = () => {
    const newReminder = prompt('Enter new reminder:');
    if (newReminder) {
      const updatedReminders = [...reminders, { text: newReminder }];
      setReminders(updatedReminders);
      updateContact(contactId, { ...contact, reminders: updatedReminders });
    }
  };

  const handleAddTransaction = () => {
    const newTransaction = prompt('Enter new transaction:');
    if (newTransaction) {
      const updatedTransactions = [...transactions, { text: newTransaction }];
      setTransactions(updatedTransactions);
      updateContact(contactId, { ...contact, transactions: updatedTransactions });
    }
  };

  return (
    <div className="individual-contact">
      <header className="header">
        <button className="back-button" onClick={() => navigate(-1)}>← Contacts</button>
      </header>
      
      <div className="contact-summary">
        <div className="contact-photo">
          {contact ? contact.avatar : <i className="bi bi-person-circle"></i>}
        </div>
        <h2>{contact ? contact.name : "Contact"}</h2>
      </div>

      <div className="tab-bar">
        <button
          className={`tab ${activeTab === 'todo' ? 'active' : ''}`}
          onClick={() => setActiveTab('todo')}
        >
          <FaCheckSquare className="icon" /> Todo
        </button>
        <button
          className={`tab ${activeTab === 'reminder' ? 'active' : ''}`}
          onClick={() => setActiveTab('reminder')}
        >
          <FaBell className="icon" /> Reminder
        </button>
        <button
          className={`tab ${activeTab === 'finance' ? 'active' : ''}`}
          onClick={() => setActiveTab('finance')}
        >
          <FaMoneyBillWave className="icon" /> Finance
        </button>
      </div>

      <div className="tab-content">
        {activeTab === 'todo' && (
          <div className="todo-content">
            {todos.map((todo, index) => (
              <div key={index} className="list-item">
                <span>{todo.text}</span>
                <FaCheckSquare className="status-icon" />
              </div>
            ))}
            <button className="add-button" onClick={handleAddTodo}>+ Add New Todo</button>
          </div>
        )}
        {activeTab === 'reminder' && (
          <div className="reminder-content">
            {reminders.map((reminder, index) => (
              <div key={index} className="list-item">
                <span>{reminder.text}</span>
                <FaBell className="status-icon" />
              </div>
            ))}
            <button className="add-button" onClick={handleAddReminder}>+ Add New Reminder</button>
          </div>
        )}
        {activeTab === 'finance' && (
          <div className="finance-content">
            {transactions.map((transaction, index) => (
              <div key={index} className="list-item">
                <span>{transaction.text}</span>
                <FaMoneyBillWave className="status-icon" />
              </div>
            ))}
            <button className="add-button" onClick={handleAddTransaction}>+ Add New Transaction</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default IndividualContact;
