const API_URL = 'http://localhost:5000/api';

export const fetchContacts = async (category) => {
  try {
    const response = await fetch(`${API_URL}/contacts/${category}`);
    if (!response.ok) {
      throw new Error('Failed to fetch contacts');
    }
    return response.json();
  } catch (error) {
    console.error('Error:', error);
    throw error;
  }
};

export const addContact = async (contactData) => {
  try {
    const response = await fetch(`${API_URL}/contacts`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(contactData),
    });
    
    if (!response.ok) {
      throw new Error('Failed to add contact');
    }
    
    return response.json();
  } catch (error) {
    console.error('Error:', error);
    throw error;
  }
};

// Fetch todos for a specific contact
export const fetchTodos = async (contactId) => {
  try {
    const response = await fetch(`${API_URL}/contacts/${contactId}/todos`);
    if (!response.ok) {
      throw new Error('Failed to fetch todos');
    }
    return response.json();
  } catch (error) {
    console.error('Error:', error);
    throw error;
  }
};

// Add a todo for a specific contact
export const addTodo = async (contactId, text) => {
  try {
    const response = await fetch(`${API_URL}/contacts/${contactId}/todos`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ text, completed: false }), // Adjust as needed
    });
    
    if (!response.ok) {
      throw new Error('Failed to add todo');
    }
    
    return response.json();
  } catch (error) {
    console.error('Error adding todo:', error);
    throw error;
  }
};


// Toggle todo completion status
export const toggleTodo = async (todoId, completed) => {
  try {
    const response = await fetch(`${API_URL}/todos/${todoId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ completed }),
    });
    
    if (!response.ok) {
      throw new Error('Failed to update todo');
    }
    
    return response.json();
  } catch (error) {
    console.error('Error updating todo:', error);
    throw error;
  }
};
