// frontend/src/components/AddContact.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { addContact } from '../api';
import '../styles.css';

function AddContact() {
  const navigate = useNavigate();
  const [contactData, setContactData] = useState({
    name: '',
    avatar: '',
    category: 'Family', // default category
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Generate avatar from first letter if not provided
      const avatarLetter = contactData.avatar || contactData.name.charAt(0).toUpperCase();
      
      await addContact({
        ...contactData,
        avatar: avatarLetter
      });
      
      // Navigate back to the contacts page for the selected category
      navigate(`/contacts?category=${contactData.category}`);
    } catch (error) {
      console.error('Error adding contact:', error);
    }
  };

  const handleChange = (e) => {
    setContactData({
      ...contactData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="add-contact-container">
      <header className="header">
        <button className="back-button" onClick={() => window.history.back()}>‚Üê Back</button>
        <div className="logo">Add New Contact</div>
      </header>
      
      <form onSubmit={handleSubmit} className="add-contact-form">
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            name="name"
            value={contactData.name}
            onChange={handleChange}
            required
            className="form-input"
          />
        </div>

        <div className="form-group">
          <label htmlFor="avatar">Avatar (Optional - First letter will be used if empty)</label>
          <input
            type="text"
            id="avatar"
            name="avatar"
            value={contactData.avatar}
            onChange={handleChange}
            maxLength="1"
            className="form-input"
          />
        </div>

        <div className="form-group">
          <label htmlFor="category">Category</label>
          <select
            id="category"
            name="category"
            value={contactData.category}
            onChange={handleChange}
            required
            className="form-input"
          >
            <option value="Family">Family</option>
            <option value="Friends">Friends</option>
            <option value="Others">Others</option>
          </select>
        </div>

        <button type="submit" className="submit-button">Add Contact</button>
      </form>
    </div>
  );
}

export default AddContact;
