// src/IndividualContact.js
import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { fetchTodos, addTodo, toggleTodo } from './api'; // Import functions from api.js
import './styles.css';
import { FaCheckSquare, FaBell, FaMoneyBillWave, FaPlusCircle } from 'react-icons/fa';

const IndividualContact = () => {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const contactId = params.get('contactId'); // Get the contact ID from URL
  const name = params.get('name'); // Get the contact name from URL
  const avatar = params.get('avatar'); // Get the contact avatar URL or ID

  const [activeTab, setActiveTab] = useState('todo'); // State to track active tab
  const [todos, setTodos] = useState([]); // State for todos
  const [reminders, setReminders] = useState([]); // Placeholder for reminders
  const [finances, setFinances] = useState([]); // Placeholder for finance entries

  // Fetch todos for the contact when component mounts
  useEffect(() => {
    const loadTodos = async () => {
      try {
        const todosData = await fetchTodos(contactId);
        setTodos(todosData);
      } catch (error) {
        console.error('Error fetching todos:', error);
      }
    };
    loadTodos();
  }, [contactId]);

  // Handle adding a new todo
  const handleAddTodo = async () => {
    const newTodoText = prompt('Enter new todo:');
    if (newTodoText) {
      try {
        const newTodo = await addTodo(contactId, newTodoText);
        setTodos([...todos, newTodo]); // Add the new todo to the list
      } catch (error) {
        console.error('Error adding todo:', error);
      }
    }
  };

  // Handle toggling todo completion
  const handleToggleTodo = async (todoId) => {
    const todo = todos.find(todo => todo._id === todoId);
    if (todo) {
      try {
        const updatedTodo = await toggleTodo(todoId, !todo.completed);
        setTodos(todos.map(t => (t._id === todoId ? updatedTodo : t)));
      } catch (error) {
        console.error('Error toggling todo completion:', error);
      }
    }
  };

  return (
    <div className="individual-contact">
      <header className="header">
        <button className="back-button" onClick={() => window.history.back()}>‚Üê Contacts</button>
      </header>

      <div className="contact-summary">
        <div className="contact-photo">
          <img src={avatar || '/default-avatar.jpg'} alt={`${name}'s avatar`} />
        </div>
        <h2>{name || "Contact"}</h2>
      </div>

      <div className="tab-bar">
        <button
          className={`tab ${activeTab === 'todo' ? 'active' : ''}`}
          onClick={() => setActiveTab('todo')}
        >
          <FaCheckSquare className="icon" /> Todo
        </button>
        <button
          className={`tab ${activeTab === 'reminder' ? 'active' : ''}`}
          onClick={() => setActiveTab('reminder')}
        >
          <FaBell className="icon" /> Reminder
        </button>
        <button
          className={`tab ${activeTab === 'finance' ? 'active' : ''}`}
          onClick={() => setActiveTab('finance')}
        >
          <FaMoneyBillWave className="icon" /> Finance
        </button>
      </div>

      <div className="tab-content">
        {activeTab === 'todo' && (
          <div className="todo-content">
            <div className="todo-header">
              <button className="add-button" onClick={handleAddTodo}>
                <FaPlusCircle className="icon" /> Add New Todo
              </button>
            </div>
            <div className="todo-list">
              {todos.length === 0 ? (
                <p>No todos found</p>
              ) : (
                todos.map(todo => (
                  <div key={todo._id} className={`list-item ${todo.completed ? 'completed' : ''}`}>
                    <input
                      type="checkbox"
                      checked={todo.completed}
                      onChange={() => handleToggleTodo(todo._id)}
                    />
                    <span>{todo.text}</span>
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {activeTab === 'reminder' && (
          <div className="reminder-content">
            <button className="add-button" onClick={() => alert('Add new reminder feature not implemented')}>
              + Add New Reminder
            </button>
            <div className="reminder-list">
              {reminders.length === 0 ? (
                <p>No reminders found</p>
              ) : (
                reminders.map((reminder, index) => (
                  <div key={index} className="list-item">
                    <span>{reminder.text}</span>
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {activeTab === 'finance' && (
          <div className="finance-content">
            <button className="add-button" onClick={() => alert('Add new transaction feature not implemented')}>
              + Add New Transaction
            </button>
            <div className="finance-list">
              {finances.length === 0 ? (
                <p>No finance records found</p>
              ) : (
                finances.map((finance, index) => (
                  <div key={index} className="list-item">
                    <span>{finance.description}</span> - <span>${finance.amount}</span>
                  </div>
                ))
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default IndividualContact;
