// src/components/IndividualContact.js
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getContact, updateContact, deleteContact } from '../api';
import '../styles.css';

function IndividualContact() {
  const { contactId } = useParams(); // Retrieve contactId from URL parameters
  const navigate = useNavigate();
  const [contactData, setContactData] = useState({
    name: '',
    avatar: '',
    category: '',
  });
  const [isEditing, setIsEditing] = useState(false);

  // Fetch contact data on component load
  useEffect(() => {
    const fetchContactData = async () => {
      try {
        const contact = await getContact(contactId);
        setContactData(contact);
      } catch (error) {
        console.error('Error fetching contact data:', error);
      }
    };
    fetchContactData();
  }, [contactId]);

  // Toggle edit mode
  const handleEdit = () => setIsEditing(!isEditing);

  // Handle input changes
  const handleChange = (e) => {
    setContactData({
      ...contactData,
      [e.target.name]: e.target.value,
    });
  };

  // Handle contact update
  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await updateContact(contactId, contactData);
      setIsEditing(false); // Exit editing mode
    } catch (error) {
      console.error('Error updating contact:', error);
    }
  };

  // Handle contact deletion
  const handleDelete = async () => {
    try {
      await deleteContact(contactId);
      navigate('/contacts'); // Redirect to contacts list after deletion
    } catch (error) {
      console.error('Error deleting contact:', error);
    }
  };

  return (
    <div className="individual-contact">
      <header className="header">
        <button className="back-button" onClick={() => navigate(-1)}>← Back</button>
        <div className="logo">Contact Details</div>
      </header>

      <div className="contact-summary">
        <div className="contact-photo">{contactData.avatar || contactData.name.charAt(0)}</div>
        {!isEditing ? (
          <>
            <h2 className="name">{contactData.name}</h2>
            <p>Category: {contactData.category}</p>
            <button onClick={handleEdit} className="add-button">Edit Contact</button>
            <button onClick={handleDelete} className="add-button" style={{ backgroundColor: '#f44336' }}>Delete Contact</button>
          </>
        ) : (
          <form onSubmit={handleUpdate} className="edit-contact-form">
            <div className="form-group">
              <label htmlFor="name">Name</label>
              <input
                type="text"
                id="name"
                name="name"
                value={contactData.name}
                onChange={handleChange}
                required
                className="form-input"
              />
            </div>

            <div className="form-group">
              <label htmlFor="avatar">Avatar (Optional)</label>
              <input
                type="text"
                id="avatar"
                name="avatar"
                value={contactData.avatar}
                onChange={handleChange}
                maxLength="1"
                className="form-input"
              />
            </div>

            <div className="form-group">
              <label htmlFor="category">Category</label>
              <select
                id="category"
                name="category"
                value={contactData.category}
                onChange={handleChange}
                required
                className="form-input"
              >
                <option value="Family">Family</option>
                <option value="Friends">Friends</option>
                <option value="Others">Others</option>
              </select>
            </div>

            <button type="submit" className="submit-button">Save Changes</button>
            <button type="button" onClick={() => setIsEditing(false)} className="add-button">Cancel</button>
          </form>
        )}
      </div>
    </div>
  );
}

export default IndividualContact;
