//src/api.js

import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

// const API_URL = 'http://localhost:5000/api';

export const fetchContacts = async (category) => {
  try {
    const response = await fetch(`${API_URL}/contacts/${category}`);
    if (!response.ok) {
      throw new Error('Failed to fetch contacts');
    }
    return response.json();
  } catch (error) {
    console.error('Error:', error);
    throw error;
  }
};

export const addContact = async (contactData) => {
  try {
    const response = await fetch(`${API_URL}/contacts`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(contactData),
    });
    
    if (!response.ok) {
      throw new Error('Failed to add contact');
    }
    
    return response.json();
  } catch (error) {
    console.error('Error:', error);
    throw error;
  }
};



export const fetchTodos = async (contactId) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/contacts/${contactId}/todos`);
    return response.data;
  } catch (error) {
    console.error('Error fetching todos:', error);
    throw error;
  }
};

export const addTodo = async (contactId, newTodoText) => {
  try {
    const newTodo = { text: newTodoText, completed: false };
    const response = await axios.post(`${API_BASE_URL}/contacts/${contactId}/todos`, newTodo);
    return response.data;
  } catch (error) {
    console.error('Error adding todo:', error);
    throw error;
  }
};

export const toggleTodo = async (contactId, todoId, isCompleted) => {
  try {
    const response = await axios.put(`${API_BASE_URL}/contacts/${contactId}/todos/${todoId}`, { completed: isCompleted });
    return response.data;
  } catch (error) {
    console.error('Error toggling todo completion:', error);
    throw error;
  }
};

