// src/Contacts.js
import React from 'react';
import { useLocation } from 'react-router-dom';
import Breadcrumb from './Breadcrumb'; // Import the Breadcrumb component
import './styles.css';

const sampleContacts = {
  Family: [
    { name: 'Alice', avatar: 'A' },
    { name: 'Bob', avatar: 'B' },
    { name: 'Charlie', avatar: 'C' },
    { name: 'David', avatar: 'D' },
  ],
  Friends: [
    { name: 'Charlie', avatar: 'C' },
    { name: 'David', avatar: 'D' },
  ],
  Others: [
    { name: 'Eve', avatar: 'E' },
    { name: 'Frank', avatar: 'F' },
  ],
};

function Contacts() {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const category = params.get('category');

  const contacts = sampleContacts[category] || [];

  return (
    <>
      <header className="header">
        <div className="logo">PeoplePixel</div>
        <input type="text" placeholder="Search..." className="search-bar" />
        <div className="profile-icon">
          <i className="bi bi-person-circle"></i>
        </div>
      </header>
      <Breadcrumb /> {/* Add Breadcrumb here */}
      <div className="container">
        {contacts.length > 0 ? (
          contacts.map((contact, index) => (
            <div key={index} className="card">
              <div className="avatar">{contact.avatar}</div>
              <p className="name">{contact.name}</p>
            </div>
          ))
        ) : (
          <p className="no-contacts">No contacts found for this category.</p>
        )}
      </div>
      <footer>
        <div className="footer-links">
          <a href="#">About</a>
          <a href="#">Help</a>
          <a href="#">Privacy Policy</a>
        </div>
        <p className="copyright">&copy; 2024 PeoplePixel. All rights reserved.</p>
      </footer>
    </>
  );
}

export default Contacts;
