import React, { useState, useEffect } from 'react';
import { useLocation, useHistory } from 'react-router-dom';
import './styles.css';
import { FaCheckSquare, FaBell, FaMoneyBillWave } from 'react-icons/fa';

const IndividualContact = () => {
  const location = useLocation();
  const history = useHistory();  // Use for redirecting
  const params = new URLSearchParams(location.search);
  const contactId = params.get('id');  // Ensure 'id' is passed in the URL
  const [activeTab, setActiveTab] = useState('todo');
  const [todos, setTodos] = useState([]);
  const [newTodoText, setNewTodoText] = useState('');

  // Fetch todos on component mount
  useEffect(() => {
    if (!contactId) {
      console.error('Contact ID is missing in URL parameters');
      return;  // Exit if contactId is null or undefined
    }

    fetch(`/api/contacts/${contactId}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch contact');
        }
        return response.json();
      })
      .then((data) => {
        setTodos(data.todos || []);
      })
      .catch((error) => console.error('Error fetching todos:', error));
  }, [contactId]);

  // Add a new todo
  const addTodo = () => {
    if (!contactId) {
      console.error('Contact ID is missing, cannot add todo');
      return;
    }

    fetch(`/api/contacts/${contactId}/todos`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ text: newTodoText, completed: false }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to add todo');
        }
        return response.json();
      })
      .then((updatedTodos) => {
        setTodos(updatedTodos);
        setNewTodoText('');
      })
      .catch((error) => {
        console.error('Error adding todo:', error);
      });
  };

  // Mark todo as complete/incomplete
  const toggleComplete = (todoId, completed) => {
    fetch(`/api/contacts/${contactId}/todos/${todoId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ completed: !completed }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to update todo');
        }
        return response.json();
      })
      .then((updatedTodo) => {
        setTodos(todos.map((todo) => (todo._id === todoId ? updatedTodo : todo)));
      })
      .catch((error) => {
        console.error('Error updating todo:', error);
      });
  };

  // Delete a todo
  const deleteTodo = (todoId) => {
    fetch(`/api/contacts/${contactId}/todos/${todoId}`, { method: 'DELETE' })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to delete todo');
        }
        return response.json();
      })
      .then((updatedTodos) => {
        setTodos(updatedTodos);
      })
      .catch((error) => {
        console.error('Error deleting todo:', error);
      });
  };

  return (
    <div className="individual-contact">
      <header className="header">
        <button className="back-button" onClick={() => history.goBack()}>← Contacts</button>
      </header>
      <div className="tab-bar">
        <button onClick={() => setActiveTab('todo')}>Todo</button>
        <button onClick={() => setActiveTab('reminder')}>Reminder</button>
        <button onClick={() => setActiveTab('finance')}>Finance</button>
      </div>
      <div className="tab-content">
        {activeTab === 'todo' && (
          <div className="todo-content">
            {todos.map((todo) => (
              <div key={todo._id} className="list-item">
                <span
                  onClick={() => toggleComplete(todo._id, todo.completed)}
                  style={{ textDecoration: todo.completed ? 'line-through' : 'none' }}
                >
                  {todo.text}
                </span>
                <button onClick={() => deleteTodo(todo._id)}>Delete</button>
              </div>
            ))}
            <input
              value={newTodoText}
              onChange={(e) => setNewTodoText(e.target.value)}
              placeholder="Add new todo"
            />
            <button onClick={addTodo}>Add Todo</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default IndividualContact;
