// src/IndividualContact.js
import React, { useState } from 'react';
import { useLocation, Link } from 'react-router-dom';
import './styles.css';
import { FaCheckSquare, FaBell, FaMoneyBillWave } from 'react-icons/fa';

const IndividualContact = () => {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const name = params.get('name'); // Get the contact name from URL
  const avatar = params.get('avatar'); // Get the contact avatar

  const [activeTab, setActiveTab] = useState('todo'); // State to track active tab
  const [todos, setTodos] = useState([]); // Sample data for todos
  const [reminders, setReminders] = useState([]); // Sample data for reminders
  const [transactions, setTransactions] = useState([]); // Sample data for transactions

  const addTodo = () => {
    // Function to add a new todo (placeholder)
  };

  const addReminder = () => {
    // Function to add a new reminder (placeholder)
  };

  const addTransaction = () => {
    // Function to add a new transaction (placeholder)
  };

  return (
    <div className="individual-contact">
      <header className="header">
        <div className="logo">PeoplePixel</div>
        <input type="text" placeholder="Search..." className="search-bar" />
        <div className="profile-icon">
          <i className="bi bi-person-circle"></i>
        </div>
      </header>
      {/* <div className="breadcrumb">
        <Link to="/">Home</Link> > <Link to={`/contacts?category=Family`}>Family</Link> > {name}
      </div> */}
      <div className="contact-summary">
        <div className="contact-photo">{avatar}</div>
        <div className="contact-info">
          <h2>{name}</h2>
        </div>
      </div>
      <div className="tab-bar">
        <button className={`tab ${activeTab === 'todo' ? 'active' : ''}`} onClick={() => setActiveTab('todo')}>
          <FaCheckSquare style={{ color: '#4CAF50', fontSize: '24px', padding: '5px', backgroundColor: '#e0f7e9', borderRadius: '50%' }} /> Todo
        </button>
        <button className={`tab ${activeTab === 'reminder' ? 'active' : ''}`} onClick={() => setActiveTab('reminder')}>
          <FaBell style={{ color: '#FFC107', fontSize: '24px', padding: '5px', backgroundColor: '#fff8e1', borderRadius: '50%' }} /> Reminder
        </button>
        <button className={`tab ${activeTab === 'finance' ? 'active' : ''}`} onClick={() => setActiveTab('finance')}>
          <FaMoneyBillWave style={{ color: '#2196F3', fontSize: '24px', padding: '5px', backgroundColor: '#e3f2fd', borderRadius: '50%' }} /> Finance
        </button>
      </div>
      <div className="tab-content">
        {activeTab === 'todo' && (
          <div className="todo-content">
            <button onClick={addTodo} className="add-button">Add New Todo</button>
            <ul>
              {todos.map((todo, index) => (
                <li key={index}>
                  <input type="checkbox" />
                  <span>{todo.text}</span>
                  <span>{todo.dueDate}</span>
                  <span>{todo.priority}</span>
                </li>
              ))}
            </ul>
          </div>
        )}
        {activeTab === 'reminder' && (
          <div className="reminder-content">
            <button onClick={addReminder} className="add-button">Add New Reminder</button>
            <ul>
              {reminders.map((reminder, index) => (
                <li key={index}>{reminder.text} - {reminder.date}</li>
              ))}
            </ul>
          </div>
        )}
        {activeTab === 'finance' && (
          <div className="finance-content">
            <button onClick={addTransaction} className="add-button">Add New Transaction</button>
            <ul>
              {transactions.map((transaction, index) => (
                <li key={index}>
                  {transaction.date} - {transaction.description} - {transaction.amount}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
      <footer>
        <div className="footer-links">
          <a href="#">About</a>
          <a href="#">Help</a>
          <a href="#">Privacy Policy</a>
        </div>
        <p className="copyright">&copy; 2024 PeoplePixel. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default IndividualContact;
