// src/Home.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import 'bootstrap-icons/font/bootstrap-icons.css';
import './styles.css';

function Home() {
  const [newCategory, setNewCategory] = useState('');
  const [showModal, setShowModal] = useState(false);

  const handleAddCategory = async () => {
    try {
      await axios.post('http://localhost:5000/api/categories', { name: newCategory });
      setNewCategory('');
      setShowModal(false);
      // Optionally refresh the category list here
    } catch (error) {
      console.error('Error adding category:', error);
      alert('Failed to add category. Please try again.');
    }
  };

  return (
    <>
      <header className="header">
        <div className="logo">PeoplePixel</div>
        <input type="text" placeholder="Search..." className="search-bar" />
        <div className="profile-icon">
          <i className="bi bi-person-circle"></i>
        </div>
      </header>
      <div className="container">
        <Link to="/contacts?category=Family" className="card">
          <i className="bi bi-heart icon"></i>
          <p>Family</p>
        </Link>
        <Link to="/contacts?category=Friends" className="card">
          <i className="bi bi-person icon"></i>
          <p>Friends</p>
        </Link>
        <Link to="/contacts?category=Others" className="card">
          <i className="bi bi-folder icon"></i>
          <p>Others</p>
        </Link>
        <button className="card add-category-card" onClick={() => setShowModal(true)}>
          <i className="bi bi-plus icon"></i>
          <p>Add Category</p>
        </button>
      </div>

      {/* Modal for Adding New Category */}
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Add New Category</h2>
            <input
              type="text"
              value={newCategory}
              onChange={(e) => setNewCategory(e.target.value)}
              placeholder="Category Name"
              required
            />
            <button onClick={handleAddCategory}>Save Category</button>
            <button onClick={() => setShowModal(false)}>Cancel</button>
          </div>
        </div>
      )}

      <footer>
        <div className="footer-links">
          <Link to="#">About</Link>
          <Link to="#">Help</Link>
          <Link to="#">Privacy Policy</Link>
        </div>
        <p className="copyright">&copy; 2024 PeoplePixel. All rights reserved.</p>
      </footer>
    </>
  );
}

export default Home;
