const API_URL = 'http://localhost:5000/api';

// Fetch contacts by category
export const fetchContacts = async category => {
  try {
    const response = await fetch(`${API_URL}/contacts/${category}`);
    if (!response.ok) {
      throw new Error('Failed to fetch contacts');
    }
    return response.json();
  } catch (error) {
    console.error('Error:', error);
    throw error;
  }
};

// Add a new contact
export const addContact = async contactData => {
  try {
    const response = await fetch(`${API_URL}/contacts`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(contactData)
    });
    if (!response.ok) {
      throw new Error('Failed to add contact');
    }
    return response.json();
  } catch (error) {
    console.error('Error:', error);
    throw error;
  }
};

// Fetch todos for a specific contact
export const fetchTodos = async contactId => {
  try {
    const response = await fetch(`${API_URL}/contacts/${contactId}/todos`);
    if (!response.ok) {
      throw new Error('Failed to fetch todos');
    }
    return response.json();
  } catch (error) {
    console.error('Error:', error);
    throw error;
  }
};

// Add a new todo for a specific contact
export const addTodo = async (contactId, newTodoText) => {
  try {
    const response = await fetch(`${API_URL}/contacts/${contactId}/todos`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        text: newTodoText,
        completed: false
      })
    });
    if (!response.ok) {
      throw new Error('Failed to add todo');
    }
    return response.json();
  } catch (error) {
    console.error('Error:', error);
    throw error;
  }
};

// Toggle completion of a todo
export const toggleTodo = async (contactId, todoId, isCompleted) => {
  try {
    const response = await fetch(`${API_URL}/contacts/${contactId}/todos/${todoId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        completed: isCompleted
      })
    });
    if (!response.ok) {
      throw new Error('Failed to toggle todo completion');
    }
    return response.json();
  } catch (error) {
    console.error('Error:', error);
    throw error;
  }
};

