//src/IndividualContact.js
import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { fetchTodos, addTodo, toggleTodo } from './api';
import './styles.css';
import { FaCheckSquare, FaBell, FaMoneyBillWave, FaPlusCircle, FaChevronLeft } from 'react-icons/fa';

const IndividualContact = () => {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const name = params.get('name');
  const avatar = params.get('avatar');
  const contactId = params.get('contactId');
  const category = params.get('category');

  const [activeTab, setActiveTab] = useState('todo');
  const [todos, setTodos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newTodoText, setNewTodoText] = useState('');
  const [newTodoPriority, setNewTodoPriority] = useState('Medium');
  const [newTodoDueDate, setNewTodoDueDate] = useState('');

  useEffect(() => {
    const loadTodos = async () => {
      if (!contactId) {
        setError('No contact ID provided');
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        const todoData = await fetchTodos(contactId);
        setTodos(todoData);
        setError(null);
      } catch (err) {
        setError('Failed to load todos: ' + err.message);
      } finally {
        setLoading(false);
      }
    };

    loadTodos();
  }, [contactId]);

  const handleAddTodo = async (e) => {
    e.preventDefault(); // Prevent page refresh
    try {
      const newTodo = {
        text: newTodoText,
        completed: false,
        priority: newTodoPriority,
        dueDate: newTodoDueDate
      };

      const response = await addTodo(contactId, newTodo);
      setTodos([...todos, response]);
      setNewTodoText('');
      setNewTodoPriority('Medium');
      setNewTodoDueDate('');
    } catch (err) {
      setError('Failed to add todo: ' + err.message);
      console.error('Error in handleAddTodo:', err);
    }
  };

  const handleToggleTodo = async (todoId, completed) => {
    try {
      const updatedTodo = await toggleTodo(todoId, !completed);
      setTodos(prevTodos =>
        prevTodos.map(todo =>
          todo._id === todoId ? updatedTodo : todo
        )
      );
    } catch (err) {
      setError('Failed to update todo: ' + err.message);
    }
  };

  return (
    <div className="individual-contact">
      <header className="header">
        <button className="back-button" onClick={() => window.history.back()}><FaChevronLeft className="icon" /> Contacts</button>
      </header>

      <div className="contact-summary">
        <div className="contact-photo">
          <img src={avatar || '/default-avatar.jpg'} alt={`${name}'s avatar`} />
        </div>
        <h2>{name || "Contact"}</h2>
      </div>

      <div className="tab-bar">
        <button className={`tab ${activeTab === 'todo' ? 'active' : ''}`} onClick={() => setActiveTab('todo')}>
          <FaCheckSquare className="icon" /> Todo
        </button>
        <button className={`tab ${activeTab === 'reminder' ? 'active' : ''}`} onClick={() => setActiveTab('reminder')}>
          <FaBell className="icon" /> Reminder
        </button>
        <button className={`tab ${activeTab === 'finance' ? 'active' : ''}`} onClick={() => setActiveTab('finance')}>
          <FaMoneyBillWave className="icon" /> Finance
        </button>
      </div>

      <div className="tab-content">
        {activeTab === 'todo' && (
          <div className="todo-content">
            {error && <div className="error-message">{error}</div>}
            
            <form className="todo-form" onSubmit={handleAddTodo}>
              <input
                type="text"
                value={newTodoText}
                onChange={(e) => setNewTodoText(e.target.value)}
                placeholder="New todo text"
                required
              />
              <select
                value={newTodoPriority}
                onChange={(e) => setNewTodoPriority(e.target.value)}
              >
                <option value="High">High</option>
                <option value="Medium">Medium</option>
                <option value="Low">Low</option>
              </select>
              <input
                type="date"
                value={newTodoDueDate}
                onChange={(e) => setNewTodoDueDate(e.target.value)}
              />
              <button type="submit"><FaPlusCircle className="icon" /> Add Todo</button>
            </form>

            <div className="todo-list">
              {loading ? (
                <p>Loading todos...</p>
              ) : todos.length === 0 ? (
                <p>No todos found</p>
              ) : (
                todos.map(todo => (
                  <div key={todo._id} className={`list-item ${todo.completed ? 'completed' : ''}`}>
                    <span>{todo.text}</span>
                    <input
                      type="checkbox"
                      checked={todo.completed}
                      onChange={() => handleToggleTodo(todo._id, todo.completed)}
                    />
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {activeTab === 'reminder' && (
          <div className="reminder-content">
            <button className="add-button">+ Add New Reminder</button>
          </div>
        )}

        {activeTab === 'finance' && (
          <div className="finance-content">
            <button className="add-button">+ Add New Transaction</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default IndividualContact;
